INSERT INTO `bd_postmeta` ( `meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(4735, 278, '_qui_somme_nous_nos_partenaires', 'field_5eb3b73382669'),
(4736, 278, 'qui_somme_nous_nos_partenaires_description', 'Découvrez l\'ensemble de nos partenaires qui nous accompagnent et soutiennent nos projets'),
(4737, 278, '_qui_somme_nous_nos_partenaires_description', 'field_5eb3b76a8266a'),
(4738, 278, 'titre_histoire', ''),
